import React from 'react'


class ShoppingCart extends React.Component{
    constructor(){
        super()
        this.state = {
            allShoppingListItems : ''}

        this.deleteShoppingItem = this.deleteShoppingItem.bind(this)


        }


componentDidMount(){
    fetch('http://127.0.0.1:8000/myapp/get_shopping_list_items')
    .then(response => response.json())
    .then (data => {
        this.setState({allShoppingListItems: data})})
}

deleteShoppingItem(event){
    var value = event.target.value
    fetch('http://127.0.0.1:8000/myapp/receive_shopping_list_delete/', 
    {
        method: 'POST',
        body: JSON.stringify({'delete': event.target.value}),
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
            // Other possible headers
        }
    })
    .then(response => response.json())
    .then(data => {
        var item_to_delete = ''
        // console.log(this.state.allShoppingListItems)    

        if (data === 'Item Deleted'){
            // Remove this element from the state
            for (var i = 0; i < this.state.allShoppingListItems.length; i++) {
                var temp = this.state.allShoppingListItems[i].fields.name
                if (temp == value){

                    console.log(this.state.allShoppingListItems)
                    this.setState(previousState => 
                        {allShoppingListItems : previousState.allShoppingListItems.splice(i)}
                    )
                    console.log(this.state.allShoppingListItems)
                    break
                }
            }    

        }
        else{
            console.log('There is an error')
        }

    } )



    }



render(){

    var shoppingItems = []
    // Adds all the shopping items from state into the above list
    if (this.state.allShoppingListItems != ''){
        for (var i = 0; i < this.state.allShoppingListItems.length; i++) {
            shoppingItems.push(this.state.allShoppingListItems[i].fields.name)
        }

    }

    const shoppingItemStyle = {marginTop: '30px'}

        return(

        <div>


        <div className="container-fluid" style = {{padding:'50px'}}>


                <div className = 'container'>
                    <h2 className="text-center" id="title" style = {{color: '#42bcf4'}}>
                        <p style = {{fontFamily : "Brush Script MT", fontSize: '150%'}}>Shopping List</p>
                    </h2>
                        <hr style = {{padding: '30px'}}/>                     

                <div className="row">
                    <div className="col-sm-6">

                     <strong style = {{marginLeft: '10%'}}>To Buy:</strong>

                    <div style = {{display: 'flex', flexDirection: 'column', marginLeft: '20%' }}>

                    {this.state.allShoppingListItems.map((value) => {
                            return (
                            <div style = {shoppingItemStyle} key = {value}> 
                            <button  onClick = {this.deleteShoppingItem} style = {{border : 'none'}} name = {'delete' + value} value = {value}>âœ–</button>
                            &nbsp;
                            <em> {value}</em>
                            </div>)
                        })}




                    </div>






                    {/* {% for item in items %} */}

                    {/* <form method = 'POST' action = "{% url 'myapp:receive_shopping_list_delete' %}">
                        {% csrf_token %}
                        <button name = 'delete' value = '{{item.name}}' type = 'submit' style = 'border:none;'>&#10006</button>{{ item.name }}
                        </form> */}
                    {/* {for } */}

                    {/* {% endfor %}   */}



                    </div>

                    <div className="col-sm-6">
                        Add:
                        <form method = 'POST' action = "{% url 'myapp:receive_shopping_list_add' %}">
                            {/* {% csrf_token %} */}
                            <input className = 'form-control'  type = 'text' name = 'add'/>
                            <br/>
                            <button className = 'btn btn-success' type = 'submit'> Submit </button>
                        </form>

                        <br/>
                        <br/>
                        Clear all:

                        <form method = 'POST' action = "{% url 'myapp:receive_shopping_list_clear' %}">
                                {/* {% csrf_token %} */}
                                <br/>
                                <button className = 'btn btn-danger' type = 'submit'> Submit </button>
                            </form>


                    </div>
                    </div>
                    </div>
                    </div>
                </div>



            )
        }
}


export default ShoppingCart
